(187, 1, '2017-11-23 06:56:06', '2017-11-23 06:56:06', '', 'chung-toi-chung-tay-xay-dung-sua-chua-ngoi-nha-mo-uoc-cua-ban', '', 'inherit', 'open', 'closed', '', 'chung-toi-chung-tay-xay-dung-sua-chua-ngoi-nha-mo-uoc-cua-ban', '', '', '2017-11-23 06:56:06', '2017-11-23 06:56:06', '', 185, 'http://localhost/vietinterview/wp-content/uploads/2017/11/chung-toi-chung-tay-xay-dung-sua-chua-ngoi-nha-mo-uoc-cua-ban.jpg', 0, 'attachment', 'image/jpeg', 0),
